import React, { useCallback, useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import classes from "./EditUser.module.css";

const EditUser = () => {
  const params = useParams();

  const [user, setUser] = useState({id: '', firstName: '', lastName: '', titleName: '', gender: '',
                                    street: '', city: '', state: '', postalCode: '',
                                    phone: '', email: ''});

  const firstNameRef = useRef('');
  const lastNameRef = useRef('');
  const titleNameRef = useRef('');

  const street = useRef('');
  const city = useRef('');
  const state = useRef('');
  const gender = useRef('');


  const postalCode = useRef('');
  const phone = useRef('');
  const email = useRef('');


  const fetchUserById = useCallback(async() => {

    // try {
      const response = await fetch(
        `http://127.0.0.1:8000/api/user/?id=${params.userId}`
      );
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      console.log(data.firstName);
      setUser({
        firstName: data.firstName,
        lastName: data.lastName,
        email: data.email,
        titleName: data.titleName,
        street: data.street,
        city: data.city,
        postalCode: data.postalCode,
        phone: data.phone,
        gender: data.gender,
        state: data.state
      });
      // const transformedMovies = data.map((movieData) => {
      //   return {
      //     id: movieData.id,
      //     gender: movieData.gender,
      //     firstName: movieData.firstName,
      //     lastName: movieData.lastName,
      //     title: movieData.titleName,
      //     street: movieData.street,
      //     titleName: movieData.titleName,
      //     city: movieData.city,
      //     state: movieData.state,
      //     postalCode: movieData.postalCode,
      //     email: movieData.email,
      //     phone: movieData.phone,
      //     url: movieData.pictureUrl,
      //   };
      // });
    // } catch (error) {}
  },[params.userId]);
  useEffect(() => {
     fetchUserById();
  }, [fetchUserById]);  
  const submitHandler = (event) => {
    event.preventDefault();
    console.log('submit');
    setUser({
        id:params.userId,
        firstName: firstNameRef.current.value,
        lastName: lastNameRef.current.value,
        email: email.current.value,
        titleName: titleNameRef.current.value,
        street: street.current.value,
        city: city.current.value,
        postalCode: postalCode.current.value,
        phone: phone.current.value,
        gender: gender.current.value,
        state: state.current.value
    });
    console.log(user);
  };

  return (
    <form onSubmit={submitHandler}>
      <div className={classes.control}>
        <label htmlFor="firstName">First Name</label>
        <input type="text" id="firstName" defaultValue={user.firstName} ref={firstNameRef} />
      </div>
      <div className={classes.control}>
        <label htmlFor="lastName">Last Name</label>
        <input type="text" id="lastName" defaultValue={user.lastName} ref={lastNameRef} />
      </div>
      <div className={classes.control}>
        <label htmlFor="titleName">Title Name</label>
        <input type="text" id="titleName" defaultValue={user.titleName} ref={titleNameRef} />
      </div>
      <div className={classes.control}>
        <label htmlFor="gender">Gender</label>
        <input type="text" id="gender" defaultValue={user.gender} ref={gender} />
      </div>
      <div className={classes.control}>
        <label htmlFor="street">street</label>
        <input type="text" id="street" defaultValue={user.street} ref={street} />
      </div>
      <div className={classes.control}>
        <label htmlFor="city">City</label>
        <input type="text" id="city" defaultValue={user.city} ref={city} />
      </div>
      <div className={classes.control}>
        <label htmlFor="state">State</label>
        <input type="text" id="state" defaultValue={user.state} ref={state} />
      </div>
      <div className={classes.control}>
        <label htmlFor="postalCode">PostalCode</label>
        <input type="text" id="postalCode" defaultValue={user.postalCode} ref={postalCode} />
      </div>
      <div className={classes.control}>
        <label htmlFor="phone">Phone</label>
        <input type="text" id="phone" defaultValue={user.phone} ref={phone} />
      </div>
      <div className={classes.control}>
        <label htmlFor="email">Email</label>
        <input type="text" id="email" defaultValue={user.email} ref={email} />
      </div>
      <button>Save</button>
    </form>
    );
};

export default EditUser;
