import React, { useEffect, useRef } from "react";
import { useParams } from "react-router-dom";
import classes from "./EditUser.module.css";

const EditUser = () => {
  const params = useParams();

  const firstNameRef = useRef('');
  const lastNameRef = useRef('');
  const titleNameRef = useRef('');

  const street = useRef('');
  const city = useRef('');
  const state = useRef('');

  const postalCode = useRef('');
  const phone = useRef('');
  const email = useRef('');


  const fetchUserById = async() => {
    // try {
      const response = await fetch(
        `http://127.0.0.1:8000/api/user/?id=${params.userId}`
      );
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      // console.log(data);
      // const transformedMovies = data.map((movieData) => {
      //   return {
      //     id: movieData.id,
      //     title: movieData.name,
      //     openingText: movieData.description,
      //     url: movieData.imageURL,
      //   };
      // });
    // } catch (error) {}
  };
  useEffect(() => {
    fetchUserById();
  }, [fetchUserById]);  
  const submitHandler = (event) => {
    event.preventDefault();
  };

  return (
    <form onSubmit={submitHandler}>
      <div className={classes.control}>
        <label htmlFor="firstName">First Name</label>
        <input type="text" id="firstName" ref={firstNameRef} />
      </div>
      <div className={classes.control}>
        <label htmlFor="lastName">Last Name</label>
        <input type="text" id="lastName" ref={lastNameRef} />
      </div>
      <div className={classes.control}>
        <label htmlFor="titleName">Title Name</label>
        <input type="text" id="titleName" ref={titleNameRef} />
      </div>
      <div className={classes.control}>
        <label htmlFor="street">street</label>
        <input type="text" id="street" ref={street} />
      </div>
      <div className={classes.control}>
        <label htmlFor="city">City</label>
        <input type="text" id="city" ref={city} />
      </div>
      <div className={classes.control}>
        <label htmlFor="state">State</label>
        <input type="text" id="state" ref={state} />
      </div>
      <div className={classes.control}>
        <label htmlFor="postalCode">PostalCode</label>
        <input type="text" id="postalCode" ref={postalCode} />
      </div>
      <div className={classes.control}>
        <label htmlFor="phone">Phone</label>
        <input type="text" id="phone" ref={phone} />
      </div>
      <div className={classes.control}>
        <label htmlFor="email">Email</label>
        <input type="text" id="email" ref={email} />
      </div>
      <button>Save</button>
    </form>
    );
};

export default EditUser;
