import React from 'react';

import classes from './Movie.module.css';

const Movie = (props) => {
  const editHandler = (id) => {
    alert(id);
  }

  return (
    <li className={classes.movie}>
      <h2>{props.id}</h2>
      <h2>{props.title}</h2>
      <h3>{props.url}</h3>
      <p>{props.openingText}</p>
      <button onClick={() => this.editHandler(props.id)}>Edit</button>
    </li>
  );
};

export default Movie;
