import React, { useState, useEffect, useCallback } from "react";

import MoviesList from "./components/MoviesList";
import AddMovie from "./components/AddMovie";
import "./App.css";
import { CSVLink } from "react-csv";
import { Route } from "react-router-dom";
import EditUser from "./components/EditUser";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [csvExport, setCsvExport] = useState({
    data: [],
    headers: [],
    filename: "",
  });
  const headers = [
    { label: "Id", key: "id" },
    { label: "Gender", key: "gender" },
    { label: "PictureUrl", key: "pictureUrl" },
    { label: "FirstName", key: "firstName" },
    { label: "LastName", key: "lastName" },
    { label: "TitleName", key: "titleName" },
    { label: "Street", key: "street" },
    { label: "City", key: "city" },
    { label: "State", key: "state" },
    { label: "PostalCode", key: "postalCode" },
    { label: "Email", key: "email" },
    { label: "Phone", key: "phone" },
    { label: "PictureUrl", key: "pictureUrl" },
  ];

  const fetchMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("http://127.0.0.1:8000/api/users");
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      console.log(data);
      const transformedMovies = data.map((movieData) => {
        return {
          id: movieData.id,
          gender: movieData.gender,
          firstName: movieData.firstName,
          lastName: movieData.lastName,
          title: movieData.titleName,
          street: movieData.street,
          titleName: movieData.titleName,
          city: movieData.city,
          state: movieData.state,
          postalCode: movieData.postalCode,
          email: movieData.email,
          phone: movieData.phone,
          url: movieData.pictureUrl,
        };
      });
      setMovies(transformedMovies);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchMoviesHandler();
  }, [fetchMoviesHandler]);

  const exportUsersHandler = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("http://127.0.0.1:8000/api/users");
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      setCsvExport({
        data: data,
        headers: headers,
        filename: "test.csv",
      });
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  };

  function addMovieHandler(movie) {
    console.log(movie);
  }

  let content = <p>Found no movies.</p>;

  if (movies.length > 0) {
    content = <MoviesList movies={movies} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    // <React.Fragment>
      <main>
        <Route path='/editUser/:userId' exact>
          <EditUser />
        </Route>
        <Route path='/' exact>
          <section>
            <CSVLink {...csvExport}>Export to CSV</CSVLink>
          </section>
          <section>
            <button onClick={exportUsersHandler}>Load users to export</button>
          </section>
          <section>{content}</section>
        </Route>
      </main>
    // </React.Fragment>
  );
}

export default App;
