import React, { useState, useEffect, useCallback } from 'react';

import MoviesList from './components/MoviesList';
import AddMovie from './components/AddMovie';
import './App.css';
import { CSVLink } from "react-csv";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [csvExport, setCsvExport] = useState(null);
  const headers = [
    { label: "Id", key: "id" },
    { label: "Name", key: "name" },
    { label: "Description", key: "description" },
    { label: "ImageUrl", key: "imageURL" }
  ];

  const fetchMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('http://127.0.0.1:8000/api/users');
      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const data = await response.json();
      console.log(data);
      const transformedMovies = data.map((movieData) => {
        return {
          id: movieData.id,
          title: movieData.name,
          openingText: movieData.description,
          url:movieData.imageURL,
        };
      });
      setMovies(transformedMovies);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchMoviesHandler();
  }, [fetchMoviesHandler]);

  const exportUsersHandler = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('http://127.0.0.1:8000/api/users');
      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const data = await response.json();
      // const transformedMovies = data.map((movieData) => {
      //   return {
      //     id: movieData.id,
      //     title: movieData.name,
      //     openingText: movieData.description,
      //     url:movieData.imageURL,
      //   };
      // });
      setCsvExport({
        data: data,
        headers: headers,
        filename: 'test.csv'
      });
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }

  function addMovieHandler(movie) {
    console.log(movie);
  }

  let content = <p>Found no movies.</p>;

  if (movies.length > 0) {
    content = <MoviesList movies={movies} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <React.Fragment>
      {/* <section>
        <AddMovie onAddMovie={addMovieHandler} />
      </section> */}
      <section>
        <button onClick={fetchMoviesHandler}>Export Users</button>
      </section>
      <section>{content}</section>
      <csvlink {...csvExport}>Export to CSV</csvlink>
    </React.Fragment>
  );
}

export default App;
